function SetGripPoint {
	NGrip = 4;
	pntGrip0 = pntOrigin;
	pntGrip1 = pntOrigin+L*vecDirection;

	pntGrip2 = pntOrigin+h*vecPlane;

	vecNormal=getLocalNormal(vecDirection,vecPlane);
	pntGrip3 = pntOrigin+vecNormal*b/2;
};

function OnMoveGripPoint{
	if (NMovingGrip == 0) {
		pntOrigin = pntGrip0;
	};

//ручка длины
	if(NMovingGrip == 1){
		L = max(vecLen(pntGrip0 - pntGrip1)/rScl,1);
		vecDirection = pntGrip1 - pntGrip0;

	};

//Ручка высоты двутавра		
	if(NMovingGrip == 2){
		rNew_h = vecLen(pntGrip0 - pntGrip2)/rScl;

		LoadInCache( seria,  h, b  );
		SelectInCache( "kFirst", "seria", "~", seria, "h", "~", rNew_h, "b", "~", b);
	};		

//Ручка ширины полки двутавра
	if(NMovingGrip == 3){
		rNew_b = 2*vecLen(pntGrip0 - pntGrip3)/rScl;

		LoadInCache( seria, b,h  );
		SelectInCache( "kFirst", "seria","~",seria, "b", "~", rNew_b,  "h", "~", h);
	};		

};


function OnMakeParameters {
	strTheName = "STP ASCHM";
	strTheType = "Profile";
	strTheSubType = "I-Shape";

	massa = mas1*L/1000;
	strDesignation =fmtText (rfName," ",  FmtDiv((@MCS_STRING_3+Designation +" "+ @MCS_STRING_2),(strMaterial +" "+ StandardMaterial),TRUE)," L="+int(L)); 
	L=(int(L*10))/10;


//Расставляем плоскости в порядке против часовой стрелки
	vecNormal=getLocalNormal(vecDirection,vecPlane);

	WP1 = Plane( pntOrigin+vecDirection*(0), vecDirection );
	WP8 = Plane( pntOrigin+vecDirection*L,-vecDirection);
	WP2 = Plane( pntOrigin-vecPlane*(0), -vecPlane );

//Правые боковые
	WP3 = Plane( pntOrigin+vecNormal*(b/2), vecNormal );
	WP4 = Plane( pntOrigin+vecNormal*(b/2)+vecPlane*h, vecNormal );

	WP5 = Plane( pntOrigin+vecPlane*(h), vecPlane );

//Левые боковые
	WP6 = Plane( pntOrigin-vecNormal*(b/2)+vecPlane*h, -vecNormal );
	WP7 = Plane( pntOrigin-vecNormal*(b/2), -vecNormal );

	WP9 = Plane( pntOrigin+vecDirection*(0)+vecPlane*(h/2),-vecDirection);//Плоскость для КМ
	WP10 = Plane( pntOrigin+vecDirection*(L)+vecPlane*(h/2),vecDirection);//Плоскость для КМ

	strSection = @SECTION_SYMBOL+" "+Designation;
	TechFilterColumnNames="Type; Designation";

	if(partName!=""){partNameCode=partName+"\n"};
	if(partName==""){partNameCode=""};
	deviationCode="";
	if(intDeviation==1){
		if (upperDeviation==-lowerDeviation){
		deviationCode="±"+fmtDigit(upperDeviation,1);
		};
		if (upperDeviation!=-lowerDeviation){
			if (upperDeviation!=0){
				if (upperDeviation>0){
					deviationCode="|+"+fmtDigit(upperDeviation,1)+"||";
					};
				if (upperDeviation<0){
					deviationCode="|"+fmtDigit(upperDeviation,1)+"||";
					};
				};
				if (lowerDeviation!=0){
					if (lowerDeviation>0){
						deviationCode=deviationCode+"||+"+fmtDigit(lowerDeviation,1)+"|";
					};
					if (lowerDeviation<0){
						deviationCode=deviationCode+"||"+fmtDigit(lowerDeviation,1)+"|";
				};
			};
		};
	};
	strPartName = partNameCode + @I_BEAM + "|" + Designation + " " + @GOST_R_57837_2017 + "|" + strMaterial + " " + StandardMaterial + "|" + "\nL=" + fmtDigit(L,1)+deviationCode+notationEnd;//нет в стандарте, по #5559
};

